com.mirth.connect.plugins.javascriptstep.script:
//

var _dateStringMSH7 = DateUtil.getCurrentDate("yyyyMMddHHmmss");
channelMap.put("_dateStringMSH7", _dateStringMSH7);

try {
	// Extract fields from the HL7 message
	var patientId = msg['PID']['PID.3']['PID.3.1'].toString();  // mrn
	var firstName = msg['PID']['PID.5']['PID.5.2'].toString();  // First 
	var lastName = msg['PID']['PID.5']['PID.5.1'].toString();   // Last 
	var dob = msg['PID']['PID.7']['PID.7.1'].toString();        // dob
	var gender = msg['PID']['PID.8']['PID.8.1'].toString();
	var patClass = msg['PV1']['PV1.2']['PV1.2.1'].toString();
	var admitLoc = msg['PV1']['PV1.3']['PV1.3.1'].toString();
	var encounterno = msg['PV1']['PV1.19']['PV1.19.1'].toString();
	var admitDt = msg['PV1']['PV1.44']['PV1.44.1'].toString();
	var insPlan = msg['IN1']['IN1.2']['IN1.2.1'].toString();
	var insNo = msg['IN1']['IN1.36']['IN1.36.1'].toString();

	//add DG1s array
	var diagnoses = [];
	// Loop DG1 segments 
	for (var i = 0; msg['DG1'][i] != undefined; i++) {
	    var dg1Code = msg['DG1'][i]['DG1.3']['DG1.3.1'].toString();      // Diagnosis Code
	    var dg1Description = msg['DG1'][i]['DG1.4']['DG1.4.1'].toString();  // Diagnosis Description

	    // Add diagnoses array
	    diagnoses.push({
	        "diagnosisCode": dg1Code,
	        "diagnosisDescription": dg1Description
	    });
	}

	// JSON object 
	var patientData = {
	    "patientId": patientId,
	    "firstName": firstName,
	    "lastName": lastName,
	    "dateOfBirth": dob,
	    "gender": gender,
	    "patClass": patClass,
	    "admitLoc": admitLoc,
	    "encounterno": encounterno,
	    "admitDt": admitDt,
	    "dateOfBirth": dob,
	    "diagnoses": diagnoses,
	    "insPlan": insPlan,
	    "insNo": insNo    
	};

	// Convert the JSON object to a string
	var jsonString = JSON.stringify(patientData, null, 2); 
	//logger.info("JSON Output: " + jsonString);
	channelMap.put("jsonString", jsonString);	

} catch(e) {
	logger.error("Exception in Main:"+ e);
}

//FHIR query
try {
	var mrn = msg['PID']['PID.3']['PID.3.1'].toString();   // Patient ID
	var fn = msg['PID']['PID.5']['PID.5.2'].toString();    // First Name
	var ln = msg['PID']['PID.5']['PID.5.1'].toString();     // Last Name
	var gn = msg['PID']['PID.8']['PID.8.1'].toString();       // gender (M/F)
	var db = msg['PID']['PID.7']['PID.7.1'].toString();    // dob (YYYYMMDD)
	// Map HL7 gender to FHIR gender
	if (gn === 'M') {
	    gn = 'male';
	} else if (gender === 'F') {
	    gn = 'female';
	}

	// Create FHIR Patient resource JSON
	var patient = {
	    "resourceType": "Patient",
	    "identifier": [
	        {
	            "use": "official",
	            "system": "http://hospital.smarthealth.org",
	            "value": mrn
	        }
	    ],
	    "name": [
	        {
	            "family": ln,
	            "given": [fn]
	        }
	    ],
	    "gender": gn,
	    "birthDate": db.slice(0, 4) + '-' + db.slice(4, 6) + '-' + db.slice(6, 8),  // Format birthdate as YYYY-MM-DD
	};

	// Convert the FHIR Patient resource to a JSON string
	var patientJson = JSON.stringify(patient);	
	//logger.info("FHIR Patient JSON: " + patientJson);
	channelMap.put("patientJson", patientJson);

} catch(e) {
	logger.error("Error in Patient JSON FHIR Resource:"+ e);
}

properties.script:
//

var statusCode = responseStatus;
var responseBody = responseMessage;

logger.info("Response Status: " + statusCode);
logger.info("Response Body: " + responseBody);

// If the response body is in JSON format, parse it
var jsonResponse;
try {
    jsonResponse = JSON.parse(responseBody);
} catch (e) {
    logger.error("Error parsing JSON response: " + e.toString());
}

// Example: Extract and log the ID of the created resource (e.g., FHIR resource ID)
if (jsonResponse && jsonResponse.id) {
    logger.info("FHIR Resource ID: " + jsonResponse.id);
}

// Store the response body and status in the response map to return to the source
responseMap.put('responseStatus', statusCode);
responseMap.put('responseBody', responseBody);

